/* eslint-disable react/jsx-pascal-case */
import React, { Fragment, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./formulaire.scss";
import Button from "../../comp/UI/button/Button";
import Header from "../../comp/UI/header/Header";
import Profil_nav from "../../comp/profil/Profil_nav";

const Form = (props) => {
  //state du formulaire
  const [name, setName] = useState("");
  const [job, setJob] = useState("");
  const [bio, setBio] = useState("");
  const [imageUrl, setimageUrl] = useState("");

  // la navigation
  const navigate = useNavigate();

  // recupération des states de app sous forme de props
  const userData = props.userData;

  // recupération des states valisation de formulaire de app sous forme de props
  const validateProfil=props.validateProfil  
  const setValidateProfil=props.setValidateProfil
  console.log("validateProfil depuis Form.js");
  console.log(validateProfil);


  // axios mise a jour des infos utilisateurs

  const handleLogin = (e) => {
    e.preventDefault();
    axios({
      method: "put",
      url: `${process.env.REACT_APP_URL}api/profil/${userData[1].id}`,
      headers: {
        "Content-Type": "multipart/form-data",
        Authorization: `Bearer ${userData[0].token}`,
      },
      data: {
        name,
        job,
        bio,
        imageUrl,
      },
    })
      .then((res) => {
        if (res.status === 200) {
          function isValidate(){
            const newValideForm = [...validateProfil]
            newValideForm[0].value = true
            setValidateProfil(
              newValideForm
            )
          }
          isValidate()
          navigate("/profil/");
        } else {
          console.log(
            "erreur lors de l'envoi du formulaire redirection impossible"
          );
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleChange_name = (event) => {
    setName(event.target.value);
  };

  const handleChange_job = (event) => {
    setJob(event.target.value);
  };

  const handleChange_imageUrl = (event) => {
    setimageUrl(event.target.value);
  };

  const handleChange_bio = (event) => {
    setBio(event.target.value);
  };

  return (
    <Fragment>
      <Header userId={props.userId}></Header>
      <Profil_nav userId={props.userId}></Profil_nav>
      <div className="form__container--main">
        <h2 className="title__profil">Remplir les données de votre Profil</h2>
        <div className="form__container--profil">
          <form
            className="profil__form"
            encType="multipart/form-data"
            method="put"
            onSubmit={handleLogin}
          >
            <label className="profil__label--title">
              Choisir votre photo de profil
            </label>
            <input
              className="profil__input"
              type="file"
              name="images"
              value={imageUrl}
              onChange={handleChange_imageUrl}
              required
            />

            <label className="profil__label">votre nom</label>
            <input
              className="profil__input"
              type="text"
              id="name"
              name="name"
              value={name}
              onChange={handleChange_name}
              required
            />

            <label className="profil__label">votre Travail</label>
            <input
              className="profil__input"
              type="text"
              id="job"
              name="job"
              value={job}
              onChange={handleChange_job}
              required
            />

            <label className="profil__label">votre Biographie</label>
            <textarea
              className="bio"
              placeholder="Ecrit ta biographie ici..."
              value={bio}
              onChange={handleChange_bio}
              required
            ></textarea>
            <Button className="btn large" value="ENVOYER" type="submit" />
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default Form;
