import axios from 'axios';


export default function Mofification_Post(props) {
    const [post, setPost] = useState("");
    const objetctId = props.objetctId
    const token = props.token
    const getRequestHome=props.getRequestHome

    const handleChange_modifPost = (event) => {
        setPost(event.target.value);
      };

      const text = "mon super texte sur react js "

      function putModifPost(e) {
        e.preventDefault();
        axios({
          method: "put",
          url: `${process.env.REACT_APP_URL}api/post/${objetctId}`,
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
             data: {
              post : text,
          },
        })
          .then((res) => {
            if (res.status === 200) {
              alert("Poste modifier avec succes !");
              getRequestHome()


              } else {
                console.log(
                  "erreur lors de l'envoi du formulaire redirection impossible"
                );
              }
          })
          .catch((err) => {
            console.log(err);
          });
      }
      console.log("props.detail");
      console.log(post);
  return (
<div className="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog">
    <div className="modal-content">
      <div className="modal-header">
        <h1 className="modal-title fs-5" id="exampleModalLabel">Modification du Poste </h1>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">
      <form
      className="container my-3 p-3"
      encType="multipart/form-data"
      method="put"
      onSubmit={putModifPost}

    >
      <label className="form-label text-capitalize">Nouveau Post</label>
      <input
        className="form-control mb-2"
        type="text"
        id="post"
        name="post"
        value={post}
        onChange={handleChange_modifPost}
        placeholder="Ã©crire vos modifications..."
