      ></input>
       <button className="btn-primary-color mt-3" value="Enregistrer" type="submit" >Enregistrer</button>
    </form>
      </div>
      <div className="modal-footer">
        <button type="button" className=" btn-secondary-color" data-bs-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>
