/* eslint-disable react/jsx-pascal-case */
import React,{useState} from "react";
import Button from "../UI/button/Button";
import axios from "axios";

const User_post = (props) => {

  //recupération du state
  const allDataUser = props.allDataUser
  const userData= props.userData
  //déclaration de state pour le post
  const [postUser,setPostUser] = useState("")

  //fonction de récupération de la valeur de l'input
function handlePostUser(e){
setPostUser(
  e.target.value
)
}
console.log(postUser);

//AXIOS pour envois du post vers la base de données
function getUserControlData(){
  axios({
      method: "post",
      url: `${process.env.REACT_APP_URL}api/post/${userData[1].id}`,

      headers: {
        Authorization: `Bearer ${userData[0].token}`,
      },
      data: {
       postUser,
      },
    })
      .then((res) => {
       console.log("données envoyer avec succés");
      })
      .catch((err) => {
        console.log(err);
      });
}
//JSX
  return (
    <div className="user__post">
      <h2>Je m'exprime...</h2>
      <form 
      className="user__container--post"
      action=""
      onSubmit={getUserControlData}
      >
        <input
          type="text"
          name="post"
          id="post"
          placeholder="Je poste mon message..."
          required
          minLength="5"
          maxLength="150"
          size="10"
          onChange={((e)=>handlePostUser(e))}
        />
        <Button 
        className="btn small" 
        value="POSTER" 
        type="submit" 
        />
      </form>
    </div>
  );
};

export default User_post;
