  const allDataUser = props.allDataUser

  console.log(allDataUser);
  return (
    <div className="user__post">
      <h2>Je m'exprime...</h2>
      <form className="user__container--post">
        <input
          type="text"
          name="post"
          id="post"
          placeholder="Je poste mon message..."
          required
          minLength="5"
          maxLength="150"
          size="10"
