        console.log("Mettre a jour le profil pour avoir l'accé");
      } else{
         accessAuthorize()
      }
      })
      .catch((err) => {
        console.log(err);
      });
}


    getRequestHome();
    getUserControlData()

  }, []);

  //JSX

  if(access[0].value === true){
    return(
      <ActiveHome userData={userData} access={access} userPost={userPost}/>
    )
  } else {
    return(
      <EmpptyHome userPost={userPost} />
    )
  }

};

export default Home;
