import React,{useState} from 'react'
import axios from "axios";
import { Navigate } from 'react-router-dom';

export default function FormModalProfil(props) {

      //state du formulaire
  const [name, setName] = useState("");
  const [job, setJob] = useState("");
  const [bio, setBio] = useState("");
  const [imageUrl, setimageUrl] =  useState({ preview: '', data: '' })

console.log(imageUrl.data);

  // recupération des states de app sous forme de props
  const userData = props.userData;

  // axios mise a jour des infos utilisateurs

  const handleName = (e) => {
    e.preventDefault();
    axios({
      method: "put",
      url: `${process.env.REACT_APP_URL}api/profil/${userData[1].id}`,
      headers: {
        "Content-Type": "multipart/form-data",
        Authorization: `Bearer ${userData[0].token}`,
      },
      data: {
        name,
      },
    })
      .then((res) => {
        if (res.status === 200) {
        alert( "Nom mis a jour !");
        <Navigate to="/profil" />

        } else {
          console.log(
            "erreur lors de l'envoi du formulaire redirection impossible"
          );
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleJob = (e) => {
    e.preventDefault();
    axios({
      method: "put",
      url: `${process.env.REACT_APP_URL}api/profil/${userData[1].id}`,
      headers: {
        "Content-Type": "multipart/form-data",
        Authorization: `Bearer ${userData[0].token}`,
      },
      data: {
        job,
      },
    })
      .then((res) => {
        if (res.status === 200) {
        alert("Travail mis à jour !");
        <Navigate to="/profil" />

        } else {
          console.log(
            "erreur lors de l'envoi du formulaire redirection impossible"
          );
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleBio = (e) => {
    e.preventDefault();
    axios({
      method: "put",
      url: `${process.env.REACT_APP_URL}api/profil/${userData[1].id}`,
      headers: {
        "Content-Type": "multipart/form-data",
        Authorization: `Bearer ${userData[0].token}`,
      },
      data: {
        bio,
      },
    })
      .then((res) => {
        if (res.status === 200) {
        alert("Biographie mis à jour !");
        <Navigate to="/profil" />

        } else {
          console.log(
            "erreur lors de l'envoi du formulaire redirection impossible"
          );
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleImg = (e) => {
    e.preventDefault();
    let formData = new FormData()
    formData.append('image', imageUrl)
    axios({
      method: "PUT",
      url: `${process.env.REACT_APP_URL}api/profil/${userData[1].id}`,
      headers: {
        "Content-Type": "multipart/form-data",
        Authorization: `Bearer ${userData[0].token}`,
      },
      data: {
      imageUrl:formData,
      },
    })
      .then((res) => {
        if (res.status === 200) {
        alert("Photo de profil mis à jour !");
        <Navigate to="/profil" />

        } else {
          console.log(
            "erreur lors de l'envoi du formulaire redirection impossible"
          );
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleChange_name = (event) => {
    setName(event.target.value);
  };

  const handleChange_job = (event) => {
    setJob(event.target.value);
  };

  const handleChange_imageUrl = (event) => {
    setimageUrl(event.target.value);
  };

  const handleChange_bio = (event) => {
    setBio(event.target.value);
  };


  return (
    <div className="form__container--profil">
    <form
      className="container my-3 p-3"
      encType="multipart/form-data"
      method="PUT"
      onSubmit={handleImg}
    >
      <label className="form-label text-capitalize">
        Choisir votre photo de profil
      </label>
      <input
        className="form-control mb-2"
        type="file"
        name="image"
        value={imageUrl.data}
        onChange={handleChange_imageUrl}
        required 
      />
       <button className="btn-primary-color mt-3" value="Enregistrer" type="submit" >Enregistrer</button>
       </form>

       <form
      className="container my-3 p-3"
      encType="multipart/form-data"
      method="put"
      onSubmit={handleName}
      required
    >
      <label className="form-label text-capitalize">votre nom</label>
      <input
        className="form-control mb-2"
        type="text"
        id="name"
        name="name"
        value={name}
        onChange={handleChange_name}
        placeholder="Votre prenom / nom "
        required  minLength="3" maxLength="10" size="10"
      ></input>
       <button className="btn-primary-color mt-3" value="Enregistrer" type="submit" >Enregistrer</button>
    </form>

    <form
      className="container my-3 p-3"
      encType="multipart/form-data"
      method="put"
      onSubmit={handleJob}
      required
    >
      <label className="form-label text-capitalize">votre Travail</label>
      <input
        className="form-control mb-2"
        type="text"
        id="job"
        name="job"
        placeholder="Votre poste dans la société Groupomania" 
        value={job}
        onChange={handleChange_job}
        required minLength="3" maxLength="25"
      />
       <button className="btn-primary-color mt-3" value="Enregistrer" type="submit" >Enregistrer</button>
    </form>

    <form
      className="container my-3 p-3"
      encType="multipart/form-data"
      method="put"
      onSubmit={handleBio}
      required

    >
      <label className="form-label text-capitalize">votre Biographie</label>
      <textarea
        className="form-control mb-2"
        placeholder="Ecrit ta biographie ici..." 
        value={bio}
        onChange={handleChange_bio}
        required minLength="10" maxLength="500"
      ></textarea>
       <button className="btn-primary-color mt-3" value="Enregistrer" type="submit" >Enregistrer</button>
    </form>
  </div>
  )
}
