import React,{useState} from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {  faHeart } from "@fortawesome/free-solid-svg-icons";
import {  faHeartBroken } from "@fortawesome/free-solid-svg-icons";
export default function Like() {
      // state like dislike
   const [like,setLike]=useState([
    {
    like:0,
    updated: false,
     }
     ])
     const [dislike,setDislike]=useState([
    {dislike:0,
     updated: false,
    }
     ])

    //  gestion des states likes et dislikes
const handleLiked = (e)=>{
    const newLike = [...like]
    newLike[0].like = e.target.value +1
    newLike[1].updated = true
    setLike(newLike)
}

const handleDisLiked = ()=>{
    const newDisLike = [...dislike]
    newDisLike[0].dislike = +1
    newDisLike[1].updated = true
    setDislike(newDisLike)
}

  return (
<div className='d-flex justify-content-center'>
    <button 
    className='btn-liked-color me-3 text-danger'
    onClick={handleLiked}>

        <FontAwesomeIcon icon={faHeart} 
         className="me-1" />{like[0].like}
    </button>
    <button 
    className='btn-liked-color'
    onClick={handleDisLiked}>

        <FontAwesomeIcon icon={faHeartBroken} 
         className="me-1" />{dislike[0].dislike}
    </button>
</div>
  )
}
