import React,{useState,useEffect} from 'react'
import Header from '../UI/header/Header'
import AllPost from './AllPost';
import Button from '../UI/button/Button';
import axios from "axios";

export default function ActiveHome(props) {

     //Recuperation des states
  const userData = props.userData;
  const access=props.access
  const userPost=props.userPost[0].value
  const allDataUser= props.allDataUser[0].value
  //state du poste cree par l'utilisateur
  const [post,setPost] = useState()

  //gestion du composant montage et update
  useEffect(() => {
//axios
function getUserControlData(event){
  event.preventDefault()
  axios({
      method: "post",
      url: `${process.env.REACT_APP_URL}api/post/`,
      headers: {
        Authorization: `Bearer ${userData[0].token}`,
      },
      data: {
       post : post,
       userId: userData[1].id,
       userName: allDataUser.name,
       userJob: allDataUser.job,
      },
    })
      .then((res) => {
     console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
}

//Methodes
const newUserPosts = userPost
console.log( "-------------newUserPosts-------------------");
console.log( newUserPosts);

// for(const prop in newUserPosts){
//   console.log(prop, newUserPosts[prop]);
// }

  //fonction de récupération de la valeur de l'input
  function handlePostUser(e){
    setPost(
      e.target.value
    )
    }

const cards = Object
.keys(newUserPosts)
.map(key=> <AllPost key={key} detail={newUserPosts[key]}/>)

//JSX
  return (
    <div className="home__container--main">
    <div className="home__container">
      <Header userId={userData} />
      <div className="user__post">
      <h2>Je m'exprime...</h2>
      <form 
      className="user__container--post"
      action=""
      onSubmit={getUserControlData}
      >
        <input
          type="text"
          name="post"
          id="post"
          placeholder="Je poste mon message..."
          required
          minLength="5"
          maxLength="150"
          size="10"
          onChange={((e)=>handlePostUser(e))}
        />
        <Button 
        className="btn small" 
        value="POSTER" 
        type="submit" 
        />
      </form>
    </div>
      <div className="post__container--root">
       {cards}
            <h2>Le file d'actualité</h2>
      </div>
    </div>
  </div>
  )
  },[]);
}
