import React from 'react'
import Header from '../UI/header/Header'
import AllPost from './AllPost';
import Button from '../UI/button/Button';
import axios from "axios";

export default function ActiveHome(props) {

     //Recuperation des states
  const userData = props.userData;
  const access=props.access
  const userPost=props.userPost
  const allDataUser= props.allDataUser[0].value
  const post = props.post
  const setPost=props.setPost

//axios
function getUserControlData(){
  axios({
      method: "post",
      url: `${process.env.REACT_APP_URL}api/post/`,

      data: {
       post : post,
       userId: userData[1],
       userName: allDataUser.name,
       userJob: allDataUser.job,
      },
    })
      .then((res) => {
        if (res.status === 200) {
      console.log("post envoyer");
        } else {
          console.log(
            "erreur lors de l'envoi du formulaire redirection impossible"
          );
        }
      })
      .catch((err) => {
        console.log(err);
      });
}

//Methodes
const newUserPosts = userPost[0].value
  //fonction de récupération de la valeur de l'input
  function handlePostUser(e){
    setPost(
      e.target.value
    )
    }

const cards = Object
.keys(newUserPosts)
.map(key=> <AllPost key={key} detail={newUserPosts[key]}/>)

  return (
    <div className="home__container--main">
    <div className="home__container">
      <Header userId={userData} />
      <div className="user__post">
      <h2>Je m'exprime...</h2>
      <form 
      className="user__container--post"
      action=""
      onSubmit={getUserControlData}
      >
        <input
          type="text"
          name="post"
          id="post"
          placeholder="Je poste mon message..."
          required
          minLength="5"
          maxLength="150"
          size="10"
          onChange={((e)=>handlePostUser(e))}
        />
        <Button 
        className="btn small" 
        value="POSTER" 
        type="submit" 
        />
      </form>
    </div>
      <div className="post__container--root">
       {cards}
            <h2>Le file d'actualité</h2>
      </div>
    </div>
  </div>
  )
}
