  const userData = props.userData[1].id
  const detail = props.detail.userId

  //state de validation
  const  access=props.access[0].value
  console.log(access);
  //conversion de la date des postes
  const dayName = new Date(props.detail.createdAt).toLocaleDateString('fr-FR', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'});


//function conditionnelle d'affichage  des boutons modifier et supprimer
const addButton = ()=>{
  return(
    <div className='d-flex mt-1'>
      <button className='text-success'>modifier</button>
      <button className='text-danger'>supprimer</button>
    </div>
  )
}

//function de controle d'affichage  des boutons modifier et supprimer
  const postAccess = ()=>{
    if(access === true && userData===detail){
      return addButton()
    } else{
      console.log("err -----------> postAccess");
    }
  }

  useEffect(()=>{

