import React from 'react'
import Button from '../UI/button/Button';

export default function AllPost(props) {

 function modifDataPost (){
  if( props.userData[1].id === props.detail.userId){
return(
  <div className='Btn__modif'> <Button className="small" value="modifier"/></div>

)
  }else{
    console.log("ne peut être modifier");
  }
}
  return (
    <div className="rootPost">

    <div className='post__card--main'>

      <div className='card__container--first'>
         <img srcSet='' alt=''></img>
      </div>

      <div className='card__container--secondary'>
        <div className='card__bio--block'>
         <p className='bio'>{props.detail.post}</p>
        </div>
        <ul className='card__info'>
         <li className='info__user'>{props.detail.userName}</li>
         <li className='info__user'>{props.detail.userJob}</li>
         <li className='info__user'>{props.detail.createdAt}</li>
        </ul>
        {modifDataPost()}
      </div>
    </div>
   </div>
  )
}
