import React,{useEffect, useState} from 'react'
import Header from '../UI/header/Header'
import AllPost from './AllPost';
import Button from '../UI/button/Button';
import axios from "axios";

export default function ActiveHome(props) {

     //Recuperation des states
  const userData = props.userData;
  const userPost=props.userPost[0].value
  const allDataUser= props.allDataUser[0].value
  //state du poste cree par l'utilisateur
  const [post,setPost] = useState()

//axios
function getUserControlData(event){
  event.preventDefault()
  axios({
      method: "post",
      url: `${process.env.REACT_APP_URL}api/post/`,
      headers: {
        Authorization: `Bearer ${userData[0].token}`,
      },
      data: {
       post : post,
       userId: userData[1].id,
       userName: allDataUser.name,
       userJob: allDataUser.job,
      },
    })
      .then((res) => {
     console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
}

  //fonction de récupération de la valeur de l'input
  function handlePostUser(e){
    setPost(
      e.target.value
    )
    }

    // boucle pour afficher les posts

const cards = Object
.keys(userPost)
.map(key=> <AllPost key={key} detail={userPost[key]} userData={userData}/>)

  return ( 

    <div className="d-flex flex-column ">
           <Header userId={userData} />
    <div className="container mt-4 d-flex flex-column justify-content-center align-items-center">
      <div className="">
      <form 
      className="p-1 mb-3 container-fluid"
      action=""
      onSubmit={getUserControlData}
      >
        <label for="inputEmail" class="form-label">Je rédige mon poste...</label>
        <input
        className='form-control'
          type="text"
          name="post"
          id="post"
          placeholder="Je poste mon message..."
          required
          minLength="5"
          maxLength="150"
          size="10"
          onChange={((e)=>handlePostUser(e))}
        />
        <Button 
        className=" btn-secondary-color fw-bold" 
        value="POSTER"  
        type="submit" 
        />
      </form>
    </div>
      <div className="post__container--root">
       {cards}
            <h2>Le file d'actualité</h2>
      </div>
    </div>
  </div>
  )
}
