/* eslint-disable react/jsx-pascal-case */
import React,{useEffect, useState}from "react";
import axios from "axios";
import Header from "../../comp/UI/header/Header";
import User_post from "../../comp/home/User_post";
import "./home.scss";

const Home = (props) => {
  //Recuperation props Token Id depuis app
  const userData = props.userData;
  console.log("userData depuis Home");
  console.log(userData);

  //State de récupération des post Data

  const {userPost, setUserPost}= useState(["oui"])
  console.log("userPost");
  console.log(userPost);

  // // recupération des states validation de formulaire de app sous forme de props
  //  const validateProfil=props.validateProfil      
  //  console.log("validateProfil depuis  Home");
  //  console.log(validateProfil);

   //didMount
   useEffect(() => {
    //recupération et traitement de la data utilisateur
    function paramsProfil() {
      axios({
        method: "get",
        url: `${process.env.REACT_APP_URL}api/post/`,

        headers: {
          Authorization: `Bearer ${userData[0].token}`,
        },
      })
        .then((res) => {
         console.log(res.data);
         function getPostData (){
          const newUserPost = [...userPost]
          newUserPost[0].value= res.data
          setUserPost(
            newUserPost
          )
          getPostData()
         }
        })
        .catch((err) => {
          console.log(err);
        });
    }
    paramsProfil();

  }, []);
  //[displayProfil, fetchData, userData]

  return (
    <div className="home__container--main">
      <div className="home__container">
        <Header userId={userData} />
        <User_post />
        <div className="post__container--root">
          <h2>Le file d'actualité</h2>
          <div className="rootPost"></div>
        </div>
      </div>
    </div>
  );
};

export default Home;
